
var Rsync = require('../rsync');

var source = process.env['USER'] + '/';
var destination = '';

/*
 * Remote configuration.
 * Set remote to false to disable remote backuo.
 */
// var remote = false;
var remote = {
  user:  process.env['USER'],
  host:  'localhost',
  shell: 'ssh'
};

var cmd = new Rsync()
  .flags('a');

// Set the source for the backup
cmd.source(source);

// Build the destination
var target = destination;
if (remote && remote.host) {
  target = remote.host + ':' + destination;
  if (remote.user) {
    target = remote.user + '@' + target;
  }
  if (remote.shell) {

  }
}
cmd.destination(target);
