var Rsync = require('./rsync');

var r = new Rsync()
    .source('d.txt')
    .destination('b')
    .progress()
    .execute(function(error, code) {
        console.log('all done');
        console.log(arguments);
    }, function(chunk) {
        console.log('stdout => ' + chunk);
    }, function(chunk) {
        console.log('stderr => ' + chunk);
    });
