var assert = require('assert');
var Rsync = require('../rsync');

describe('rsync.patterns', function() {

    it('should accept a single String pattern', function() {
        var rsync = new Rsync();
        rsync.patterns('-.git');

        assert(rsync._patterns.length, 1);

        var pattern = rsync._patterns[0];
        assert(typeof(pattern), 'object');
        assert.equal(pattern.action, '-');
        assert.equal(pattern.pattern, '.git');
    });

    it('should accept a single Object pattern', function() {
        var rsync = new Rsync();
        rsync.patterns({ action: '-', pattern: '.git' });
    });

    it('should accept patterns as a single Array', function() {
        var rsync = new Rsync();
        rsync.patterns(['+/bar', { action: '-', pattern: '.git' }]);
    });

    it('should accept patterns as a list of arguments', function() {
        var rsync = new Rsync();
        rsync.patterns('+/bar', { action: '-', pattern: '.git' });
    });

});

